import React, { useEffect, useState, useRef } from "react";
// import UserCard from "../UserCard";
import AnanomUserCard from "./AnanomUserCard";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import MsgDisplay from "../message/MsgDisplay";
import AnanomMsgDisplay from "./AnanomMsgDisplay"
import Icons from "../Icons";
import { GLOBALTYPES } from "../../redux/actions/globalTypes";
import { imageShow, videoShow } from "../../utils/mediaShow";
import { imageUpload } from "../../utils/imageUpload";
// import {
//   addMessage,
//   getMessages,
//   MESSAGE_TYPES,
// } from "../../redux/actions/messageAction";
import LoadIcon from "../../images/loading.gif";

const AnanomUserChat = (props) => {
  const googleId = props.googleId;
  const conversation = props.conversation;
    const [messages, setMessages] = useState([]);
  const { auth, message, theme, socket } = useSelector((state) => state);
  const dispatch = useDispatch();
  const [incomingMessage, setIncomingMessage] = useState(null);
  // const [value, setValue] = useState();
  // const [user, setUser] = useState([]);
  const [text, setText] = useState("");
  // const [page, setPage] = useState(0);
  // const [data, setData] = useState([]);
  // const { id } = useParams();
  const [media, setMedia] = useState([]);
  const [loadMedia, setLoadMedia] = useState(false);

  const refDisplay = useRef();
  const pageEnd = useRef();

  useEffect(() => {
    socket.on("getAnanomMessage", (data) => {
      setIncomingMessage({
        sender: data.senderId,
        receiverId: auth.user._id,
        text: data.text,
        media: data.newArr,
        createdAt: data.createdAt,
      });
    });
  }, [socket]);

  useEffect(() => {
    incomingMessage &&
      conversation?.members?.includes(incomingMessage.sender) &&
      setMessages((prev) => [...prev, incomingMessage]);
  }, [incomingMessage, conversation]);

  // useEffect(() => {
  //   const newData = message.data.filter(
  //     (item) => item.sender === auth.user._id || item.sender === id
  //   );
  //   setData(newData);
  // }, [message.data, auth.user._id, id]);

  // useEffect(() => {
  //   const newUser = message.users.find((user) => user._id === id);
  //   if (newUser) {
  //     setUser(newUser);
  //   }
  // }, [message.users, id]);

  const handleChangeMedia = (e) => {
    const files = [...e.target.files];
    let err = "";
    let newMedia = [];

    files.forEach((file) => {
      if (!file) {
        return (err = "File does not exist.");
      }
      if (file.size > 1024 * 1024 * 5) {
        return (err = "Image size must be less than 5 mb.");
      }
      return newMedia.push(file);
    });
    if (err) {
      dispatch({ type: GLOBALTYPES.ALERT, payload: { error: err } });
    }
    setMedia([...media, ...newMedia]);
  };

  const handleDeleteMedia = (index) => {
    const newArr = [...media];
    newArr.splice(index, 1);
    setMedia(newArr);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim() && media.length === 0) return;
    setText("");
    setMedia([]);

    setLoadMedia(true);

    let newArr = [];
    if (media.length > 0) newArr = await imageUpload(media);

    // const msg = {
    //   sender: auth.user._id,
    //   recipient: id,
    //   text,
    //   media: newArr,
    //   createdAt: new Date().toISOString(),
    // };
    let message = {
      sender: auth.user._id,
      receiverId: googleId,
      receieverSocketID:socketID,
      text,
      media: newArr,
      createdAt: new Date().toISOString(),
    };
    conversation?.members?.includes(auth.user._id) &&
      setMessages((prev) => [...prev, message]);
    socket.emit("sendAnanomMessage", message);
    setLoadMedia(false);
    // await dispatch(addMessage({ msg, auth, socket }));
    if (refDisplay.current) {
      refDisplay.current.scrollIntoView({
        behaviour: "smooth",
        block: "end",
      });
    }
  };

  // useEffect(() => {
  //   if (id) {
  //     const getMessagesData = async () => {
  //       dispatch({
  //         type: MESSAGE_TYPES.GET_MESSAGES,
  //         payload: { messages: [] },
  //       });

  //       setPage(1);
  //       await dispatch(getMessages({ auth, id }));
  //       if (refDisplay.current) {
  //         refDisplay.current.scrollIntoView({
  //           behaviour: "smooth",
  //           block: "end",
  //         });
  //       }
  //     };

  //     getMessagesData();
  //   }
  // }, [id, dispatch, auth]);

  // load more

  // useEffect(() => {
  //   const observer = new IntersectionObserver(
  //     (entries) => {
  //       if (entries[0].isIntersecting) {
  //         setPage((p) => p + 1);
  //       }
  //     },
  //     {
  //       threshold: 0.1,
  //     }
  //   );
  //   observer.observe(pageEnd.current);
  // }, [setPage]);

  // useEffect(() => {
  //   if (message.resultData >= (page - 1) * 9 && page > 1) {
  //     dispatch(getMessages({ auth, id, page }));
  //   }
  // }, [message.resultData, page, id, auth, dispatch]);

  useEffect(() => {
    if (refDisplay.current) {
      refDisplay.current.scrollIntoView({
        behaviour: "smooth",
        block: "end",
      });
    }
  }, [text]);

  return (
    <>
      <div className="message_header">
        {/* {user.length !== 0 && ( */}
        <AnanomUserCard
          find={props.find}
          setFind={props.setFind}
          end={props.end}
          setEnd={props.setEnd}
          bcode={props.bcode}
          setBcode={props.setBcode}
        >
          <i className="fas fa-trash text-danger" />
        </AnanomUserCard>
        {/* )} */}
      </div>

      <div
        className="chat_container"
        style={{ height: media.length > 0 ? "calc(100% - 180px)" : "" }}
      >
        <div className="chat_display" ref={refDisplay}>
          <button style={{ marginTop: "-25px", opacity: 0 }} ref={pageEnd}>
            Load..
          </button>

          {messages.map((msg, index) => (
            <div key={index}>
              {msg.sender !== auth.user._id && (
                <div className="chat_row other_message">
                  <AnanomMsgDisplay msg={msg} theme={theme} />
                </div>
              )}
              {msg.sender === auth.user._id && (
                <div className="chat_row you_message">
                  <MsgDisplay user={auth.user} msg={msg} theme={theme} />
                </div>
              )}
            </div>
          ))}
          {loadMedia && (
            <div className="chat_row you_message">
              <img src={LoadIcon} alt="Loading..." />
            </div>
          )}
        </div>
      </div>

      <div
        className="show_media"
        style={{ display: media.length > 0 ? "" : "none" }}
      >
        {media.map((item, index) => (
          <div key={index} id="file_media">
            {item.type.match(/video/i)
              ? videoShow(URL.createObjectURL(item), theme)
              : imageShow(URL.createObjectURL(item), theme)}
            <span onClick={() => handleDeleteMedia(index)}>&times;</span>
          </div>
        ))}
      </div>

      <form className="chat_input" onSubmit={handleSubmit}>
        <input
          placeholder="Type a message."
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          style={{
            filter: theme ? "invert(1)" : "invert(0)",
            background: theme ? "#040404" : "",
            color: theme ? "white" : "",
          }}
        />
        <Icons setContent={setText} content={text} theme={theme} />
        <div className="file_upload">
          <i className="fas fa-image color-c1" />
          <input
            type="file"
            name="file"
            id="file"
            multiple
            accept="image/*,video/*"
            onChange={handleChangeMedia}
          />
        </div>
        <button
          type="submit"
          disabled={text || media.length > 0 ? false : true}
          className="material-icons"
        >
          near_me
        </button>
      </form>
    </>
  );
};

export default AnanomUserChat;
