import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../../redux/actions/authAction";
import { GLOBALTYPES } from "../../redux/actions/globalTypes";
// import {ChatIcon} from "@material-ui/icons";
import Avatar from "../Avatar";
import NotifyModal from "../NotifyModal";

const Navbar = () => {
  const [unreadNotifications, setUnreadNotifications] = useState([]);
  const navLinks = [
    { label: "Home", icon: "home", path: "/" },
    { label: "AnanomMessage", icon: "near_me", path: "/ananommessage" },
    { label: "Discover", icon: "explore", path: "/discover" },
  ];

  const { auth, theme, notify } = useSelector((state) => state);
  const dispatch = useDispatch();
  const { pathname } = useLocation();

  const isActive = (pn) => {
    if (pn === pathname) return "active";
  };

  useEffect(() => {
    const newArr = notify.data.filter((item) => item.isRead === false);
    setUnreadNotifications(newArr);
  }, [notify]);

  return (
    <div className="menu">
      <ul className="navbar-nav flex-row mb-2 mb-lg-0">
        <li id="about" className="nav-item">
          <a href="/about" className="nav-links">
            About
          </a>
        </li>
        <li className="nav-item">
          <div className="nav-links">
            <button id="navbar-signup" onClick={() => console.log()}>
              <a href="/register" className="nav-links">SignUp</a>
            </button>
          </div>
        </li>
        <li className="nav-item">
          <div className="nav-links">
            <button className="button" id="navbar-login">
              <a href="/login" className="nav-links">LogIn</a>
            </button>
          </div>
        </li>
      </ul>
    </div>
  );
};

export default Navbar;
