import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../../redux/actions/authAction";
import { GLOBALTYPES } from "../../redux/actions/globalTypes";
// import {ChatIcon} from "@material-ui/icons";
import Avatar from "../Avatar";
import NotifyModal from "../NotifyModal";

const Navbar = () => {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [navbar, setNavbar] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 780) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener("resize", showButton);

  const changeBackground = () => {
    if (window.scrollY >= 75) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };

  window.addEventListener("scroll", changeBackground);
  const [unreadNotifications, setUnreadNotifications] = useState([]);
  // const navLinks = [
  //   { label: "Home", icon: "home", path: "/" },
  //   { label: "AnanomMessage", icon: "near_me", path: "/ananommessage" },
  //   { label: "Discover", icon: "explore", path: "/discover" },
  // ];

  const { auth, theme, notify } = useSelector((state) => state);
  const dispatch = useDispatch();
  const { pathname } = useLocation();

  const isActive = (pn) => {
    if (pn === pathname) return "active";
  };

  useEffect(() => {
    const newArr = notify.data.filter((item) => item.isRead === false);
    setUnreadNotifications(newArr);
  }, [notify]);

  return (
    <div className="menu">
      <ul className="navbar-nav flex-row mb-2 mb-lg-0">
        <div className="homepage_navbar_container">
          <a href="/" className="navbar-logo" onClick={closeMobileMenu}>
            <img alt="brand-logo" id="logo" src={Logo} />
          </a>
          <div className="menu-icon" onClick={handleClick}>
            <img alt="bars" className="bars" src={Bars} />
          </div>
          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <div className="mobileHead nav-item">
              <a href="/" className="navbar-logo" onClick={closeMobileMenu}>
                <img alt="logo" id="logo" src={Logo} />
              </a>
            </div>
            <div className="menu-icon" onClick={closeMobileMenu}>
              <i className="fas fa-times" />
            </div>

            <li id="about" className="nav-item">
              <a href="/about" className="nav-links" onClick={closeMobileMenu}>
                About
              </a>
            </li>
            <li className="nav-item">
              <div className="nav-links" onClick={closeMobileMenu}>
                <button id="navbar-signup" onClick={() => console.log()}>
                  <Link to={"/register"}>SignUp</Link>
                </button>
              </div>
            </li>
            <li className="nav-item">
              <div className="nav-links" onClick={closeMobileMenu}>
                <button className="button" id="navbar-login">
                  <Link to={"/login"}>LogIn</Link>
                </button>
              </div>
            </li>
          </ul>
        </div>
      </ul>
    </div>
  );
};

export default Navbar;
