import React from 'react'
import { ToastContainer, toast } from "react-toastify";

const Toast = ({ msg, handleShow, bgColor }) => {
  console.log(msg)
  useEffect(() => {

  })
  if (msg.title === "Error") {
    toast.error(msg.body, { theme: "colored" });
  }
  if (msg.title === "Success") {
    toast.success(msg.body, { theme: "colored" });
  }
    return (
      <div
        className={`toast show position-fixed text-light ${bgColor} `}
        style={{ top: "5px", right: "5px", minWidth: "200px", zIndex: 50 }}
      >
        <ToastContainer></ToastContainer>
        <div className={`toast-header text-light ${bgColor}`}>
          <strong className="mr-auto text-light">{msg.title}</strong>
          <button
            className="ml-auto mb-1 close text-light"
            data-dismiss="toast"
            style={{
              border: "none",
              background: "none",
              fontSize: "30px",
              right: 0,
            }}
            onClick={handleShow}
          >
            &times;
          </button>
        </div>
        <div className="toast-body">{msg.body}</div>
      </div>
    );
};

export default Toast
