import { MESSAGE_TYPES } from "../actions/saveConversation";

const initialState = {
    // message:[],
//   users: [],
//   resultUsers: 0,
  data: [],
//   resultData: 0,
  firstLoad: false,
};

const ananomMessageReducer = (state = initialState, action) => {
  switch (action.type) {
    case MESSAGE_TYPES.ADD_MESSAGE:
      return {
        ...state,
        data: [...state.data, action.payload],
        // users: state.users.map((user) =>
        //   user._id === action.payload.recipient ||
        //   user._id === action.payload.sender
        //     ? {
        //         ...user,
        //         text: action.payload.text,
        //         media: action.payload.media,
        //       }
        //     : user
        // ),
      };

    case MESSAGE_TYPES.GET_CONVERSATIONS:
      return {
        ...state,
        firstLoad: true,
      };

    case MESSAGE_TYPES.RSE_CONVERSATIONS:
      return {
        ...state,
        data: [],
        resultData: action.payload.result,
      };

    default:
      return state;
  }
};

export default ananomMessageReducer;
